//1. render songs
//2. Scroll top
//3. Play/Pause/Seek
//4. CD Rotate
//5. Next/Prev
//6. Random
//7. Next/Repeat when Ended
//8. Active song
//9. Scrool active song into view 
//10. Play song when click

const $ = document.querySelector.bind(document);
const $$ = document.querySelectorAll.bind(document);

const PLAYERS_STORAGE_KEY = 'F8_PLAYER';

const heading = $('h2');
const cdThumb = $('.cd-thumb');
const audio = $('#audio');
const cd = $('.cd');
const playBtn = $('.btn.btn-toggle-play');
const player = $('.player');
const progress = $('#progress');
const btnNext = $('.btn-next');
const btnPrev = $('.btn-prev');
const btnRepeat = $('.btn-repeat');
const btnRandom = $('.btn-random');
const playList = $('.playlist');

const app = {
  currentIndex: 0,
  isPlaying: false,
  isRandom: false,
  isRepeat: false,
  config: JSON.parse(localStorage.getItem(PLAYERS_STORAGE_KEY)) || {},
  songs: [
    {
      name: "Bát Nhã Tâm Kinh",
      singer: "Thích Trí Thoát",
      path: "https://tainhac123.com/listen/bat-nha-tam-kinh-21-bien-va.ohZLZJ29D4xo.html",
      image: "https://tranhchuthap.com/wp-content/uploads/2019/11/VS466.jpg"
    },
    {
      name: "Kinh Dược Sư",
      singer: "Thích Trí Thoát",
      path: "https://tainhac123.com/listen/kinh-duoc-su-thich-tri-thoat.Ciqk8XqKxjJX.html",
      image: "data:image/jpeg;base64,"
    },
    {
      name: "Chú Đại Bi",
      singer: "Thích Trí Thoát",
      path: "https://tainhac123.com/listen/chu-dai-bi-thich-tri-thoat.sPBiXOrd2T.html",
      image:
        "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT5UBl2wn2Bk4NGutVkNIHCUAOh12LQElT8hg&usqp=CAU"
    },
    {
      name: "Kinh Lăng Nghiêm",
      singer: "Thích Trí Thoát",
      path: "https://tainhac123.com/listen/kinh-lang-nghiem-thich-tri-thoat.2OI5GLguAEt4.html",
      image:
        "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQzgOqshULk7iP4gI0z0JrbpW2s_ufXAXcSIA&usqp=CAU"
    },
    {
      name: "Nguyện Về Cỏi Phật",
      singer: "Thanh Ngân",
      path:
        "https://tainhac123.com/listen/nguyen-ve-coi-phat-thanh-ngan-nsut.3aieNjiv7R.html",
      image: "data:image/jpeg;base64,"
    },
    {
      name: "Lạy Phật Quan Âm",
      singer: "Thanh Ngân",
      path:
        "https://tainhac123.com/listen/lay-phat-quan-am-thanh-ngan-nsut.3SwW9wkTh1h7.html",
      image: "data:image/jpeg;base64,"
    },
    {
      name: "Ai Gánh Cuộc Trầm Luân",
      singer: "Hoài Lâm",
      path:
        "https://tainhac24h.com/get/song/api/87551",
      image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTyUtFfEf4LKzQ1LlCh12IdZNWwYok6amEE4w&usqp=CAU"
    }
  ],
  setConfig: function (key, value) {
    this.config[key] = value;
    localStorage.setItem(PLAYERS_STORAGE_KEY, JSON.stringify(this.config));
  },
  render: function() {
    var htmls = this.songs.map((song, index) =>{
      return `
      <div class="song ${index === this.currentIndex ? 'active' : ''}" data-index="${index}">
      <div class="thumb" style="background-image: url('${song.image}')">
      </div>
      <div class="body">
        <h3 class="title">${song.name}</h3>
        <p class="author">${song.singer}</p>
      </div>
      <div class="option">
        <i class="fas fa-ellipsis-h"></i>
      </div>
    </div>
      `;
    } );
    playList.innerHTML = htmls.join('');
  }, 

  defineProperties: function() {
    Object.defineProperty(this, 'currentSong',{
      get: function() {
        return this.songs[this.currentIndex];
      }
    })
  },

  loadCurrentSong: function() {
    heading.textContent = this.currentSong.name;
    cdThumb.style.backgroundImage = `url('${this.currentSong.image}')`;
    audio.src = this.currentSong.path;
  },
  loadConfig: function() {
    this.isRandom = this.config.isRandom; 
    this.isRepeat = this.config.isRepeat;
  },
  prevSong: function() {
    this.currentIndex--;
    if(this.currentIndex < 0 ){
      this.currentIndex = this.songs.length -1;
    }
    this.loadCurrentSong();
  },
  nextSong: function() {
    this.currentIndex++;
    if(this.currentIndex >= this.songs.length){
      this.currentIndex = 0;
    }
    this.loadCurrentSong();
  },
  playRandomSong: function() {
    let newIndex = 0;
    do{
      newIndex = Math.floor(Math.random() * this.songs.length);
      console.log(newIndex);
    }while(newIndex == this.currentIndex || newIndex == 0)
  
    this.currentIndex =  newIndex;
    this.loadCurrentSong();
  },
  scrollToActiveSong: function() {
    setTimeout(() => {
      $('.song.active').scrollIntoView({
        behavior: 'smooth',
        block: 'center',
        
      });
    },100)
  },
  handleEvent: function() {
    const cdWidth = cd.offsetWidth;
    const _this = this;
    //Handling CD rotation/stop
    const cdThumAnimate = cdThumb.animate([{
      transform: 'rotate(360deg)'}
    ], {duration: 10000, //10second
        iterations: Infinity
    });
    cdThumAnimate.pause();
    //Handle Zoom/Zout CD
    document.onscroll = function() {
      const scrollTop = window.scrollY || document.documentElement.scrollTop;
      const newcdWidth = cdWidth - scrollTop;
      cd.style.width = newcdWidth > 0 ? newcdWidth + 'px' : 0;
      cd.style.opacity = newcdWidth / cdWidth ;

    }
    //Handle when click play
    playBtn.onclick = function() {
      if(_this.isPlaying){
        audio.pause();
      }else{
        audio.play();
      }
      
    }
    // When Click button Space
      document.onkeydown = function(e) {
        if(e.code === 'Space' || e.code === 'Escape'){
          if(!_this.isPlaying){
            audio.play() // Audio Play
        }
        else{
            audio.pause() //Audio Pause
        }
        }
      }
    //When song is playing
    audio.onplay = function() {
        _this.isPlaying = true;
        player.classList.add('playing');
        cdThumAnimate.play();
        _this.render();
        _this.scrollToActiveSong();
    }
    //When song is pause
    audio.onpause = function() {
      _this.isPlaying = false;
      player.classList.remove('playing');
      cdThumAnimate.pause();
    }
    //When the song playback progress changes
    audio.ontimeupdate = function() {
      if(audio.duration){
        progressPercent = Math.floor(audio.currentTime / audio.duration * 100);
        progress.value = progressPercent;
      }
    }
    //Handling when rewinding songs
    progress.onchange = function(e) {
      const seek = e.target.value / 100 * audio.duration;
      audio.currentTime = seek;
    }
    //Handle when click Next
    btnNext.onclick = function() {
      if(_this.isRandom) {
        _this.playRandomSong();
      }else{
        _this.nextSong();
      }
      audio.play();
      _this.scrollToActiveSong();
    }
    //Handle when click Prev
    btnPrev.onclick = function() {
      if(_this.isRandom) {
        _this.playRandomSong();
      }else{
        _this.prevSong();
      }
      audio.play();
      _this.scrollToActiveSong();
    }
    //Handle when click Random
    btnRandom.onclick = function() {
      _this.isRandom = !_this.isRandom;
      _this.setConfig('isRandom', _this.isRandom);
      btnRandom.classList.toggle('active', _this.isRandom);
    }
    //Handle next song when audio ended
    audio.onended = function() {
      if(_this.isRepeat){
        audio.play();
      }else{
        _this.nextSong();
        audio.play();
      }
      
    }
    //Handle when click Repeat
    btnRepeat.onclick = function() {
      _this.isRepeat = !_this.isRepeat;
      _this.setConfig('isRepeat', _this.isRepeat);
      btnRepeat.classList.toggle('active', _this.isRepeat);
    }
    //Handle when click on playlist
    playList.onclick = function(e) {
      const songNode = e.target.closest('.song:not(.active)');
      if(songNode || e.target.closest('.option')) {
        //Handle when click on songs
        if(songNode) {
          _this.currentIndex = Number(songNode.dataset.index);
          _this.loadCurrentSong();
          _this.render();
          audio.play();
        }
        //Handle when click on option
      }
    }
  },
  start: function() {
    //Load config
    this.loadConfig();
    //Define properties for Object
    this.defineProperties();
    
    //Playing with Random song
    this.playRandomSong();
    //Load the first Song when star App
    this.loadCurrentSong();
    //Listen/ reject events in DOM
    this.handleEvent();
    //Render htmls for browser
    this.render();
    btnRepeat.classList.toggle('active', this.isRepeat);
    btnRandom.classList.toggle('active', this.isRandom);
  }
}
//start App
app.start();
